import{_ as r}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as n,d as t,a as s,f as g}from"./app.0a50f910.js";const a={},i=s("p",null,"《若能绽放光芒》（光るなら）是由Goose house乐队演唱的歌曲，作为日本动漫《四月是你的谎言》中的第一首片头曲，歌曲爽快的曲风令人炫目，以至于很多观众就是因为这首歌才开始接触到作品。该歌由Goose house为动画《四月是你的谎言》量身打造。",-1),e=g('<h1 id="光るなら-若能绽放光芒" tabindex="-1"><a class="header-anchor" href="#光るなら-若能绽放光芒" aria-hidden="true">#</a> 光るなら(若能绽放光芒)</h1><h2 id="part-1" tabindex="-1"><a class="header-anchor" href="#part-1" aria-hidden="true">#</a> Part 1</h2><p><strong>雨上（あめあ）がりの虹（にじ）も</strong><br><strong>a me a ga ri no ni ji mo</strong><br><strong>雨过天晴的彩虹</strong></p><p><strong>凛（りん）と咲（さ）いた花（はな）も</strong><br><strong>rin to sa i ta ha na mo</strong><br><strong>凛然绽放的花朵</strong></p><p><strong>色（いろ）づき溢（あふ）れ出（だ）す</strong><br><strong>i ro zu ki a fu re da su</strong><br><strong>色彩随之满溢缤纷</strong></p><p><strong>茜色（あかねいろ）の空（そら） 仰（あお）ぐ君（きみ）に</strong><br><strong>a ka ne i ro no so ra a o gu ki mi ni</strong><br><strong>望着那个仰望绯红色天空的你</strong></p><p><strong>あの日（ひ） 恋（こい）に落（お）ちた</strong><br><strong>a no hi ko i ni o ti ta</strong><br><strong>就在那天 我坠入了爱河</strong></p><p><strong>瞬間（しゅんかん）のドラマチック</strong><br><strong>syun kan no do ra ma ti kku</strong><br><strong>戏剧化的那一瞬间</strong></p><p><strong>フィルムの中（なか）の1（ひと）コマも</strong><br><strong>fui ru mu no na ka no hi to ko ma mo</strong><br><strong>如荧幕中的特写镜头</strong></p><p><strong>消（き）えないよ 心（こころ）に刻（きざ）むから</strong><br><strong>ki e na i yo ko ko ro ni ki za mu ka ra</strong><br><strong>早已深深铭刻在我心中 永远不会消失</strong></p><p><strong>君（きみ）だよ 君（きみ）なんだよ 教（おし）えてくれた</strong><br><strong>ki mi da yo ki mi nan da yo o shi e te ku re ta</strong><br><strong>是你 是你 告诉我</strong></p><p><strong>暗闇（くらやみ）も光（ひか）るなら</strong><br><strong>ku ra ya mi mo hi ka ru na ra</strong><br><strong>若能在黑暗中绽放光芒</strong></p><p><strong>星空（ほしぞら）になる</strong><br><strong>ho shi zo ra ni na ru</strong><br><strong>便能化作星空</strong></p><p><strong>悲（かな）しみを笑顔（えがお）に</strong><br><strong>ka na shi mi wo e ga o ni</strong><br><strong>悲伤化作笑颜</strong></p><p><strong>もう隠（かく）さないで</strong><br><strong>mou ka ku sa nai de</strong><br><strong>已经无需隐藏</strong></p><p><strong>煌（きら）めくどんな星（ほし）も</strong><br><strong>ki ra me ku don na ho shi mo</strong><br><strong>漫天闪耀着的星光</strong></p><p><strong>君（きみ）を照（て）らすから</strong><br><strong>ki mi wo te ra su ka ra</strong><br><strong>都会为你照亮前行</strong></p><h2 id="part-2" tabindex="-1"><a class="header-anchor" href="#part-2" aria-hidden="true">#</a> Part 2</h2><p><strong>眠（ねむ）りも忘（わす）れて 迎（むか）えた朝日（あさひ）が</strong><br><strong>ne mu ri mo wa su re te mu ka e ta a sa hi ga</strong><br><strong>一夜未眠迎来的朝阳</strong></p><p><strong>やたらと突（つ）き刺（さ）さる</strong><br><strong>ya ta ra to tsu ki sa sa ru</strong><br><strong>却愈加刺痛我心</strong></p><p><strong>低気圧（ていきあつ）運（はこ）ぶ 頭痛（ずつう）だって</strong><br><strong>te i ki a tsu ha ko bu zu tsuu da tte</strong><br><strong>令人烦闷的头痛</strong></p><p><strong>忘（わす）れる 君（きみ）に会（あ）えば</strong><br><strong>wa su re ru ki mi ni a e ba</strong><br><strong>只要见到你 便能瞬间遗忘</strong></p><p><strong>静寂（せいじゃく）はロマンティック</strong><br><strong>se i jya ku wa ro man tei kku</strong><br><strong>静寂编制你我的罗曼史</strong></p><p><strong>紅茶（こうちゃ）に溶（と）けたシュガーのように</strong><br><strong>kou tya ni to ke ta syu gaa no you ni</strong><br><strong>如红茶里融化的砂糖</strong></p><p><strong>全身（ぜんしん）に巡（めぐ）るよ 君（きみ）の声（こえ）</strong><br><strong>zen shin ni me gu ru yo ki mi no ko e</strong><br><strong>你的声音萦绕全身</strong></p><p><strong>君（きみ）だよ 君（きみ）なんだよ</strong><br><strong>ki mi da yo ki mi nan da yo</strong><br><strong>是你 是你</strong></p><p><strong>笑顔（えがお）をくれた</strong><br><strong>e ga o wo ku re ta</strong><br><strong>带给我笑容</strong></p><p><strong>涙（なみだ）も光（ひか）るなら</strong><br><strong>na mi da mo hi ka ru na ra</strong><br><strong>泪滴若能绽放光芒</strong></p><p><strong>流星（りゅうせい）になる</strong><br><strong>ryuu se i ni na ru</strong><br><strong>便能化作流星</strong></p><p><strong>傷（きず）付（つ）いたその手（て）を</strong><br><strong>ki zu tsu i ta so no te o</strong><br><strong>这伤痕累累的双手</strong></p><p><strong>もう離（はな）さないで</strong><br><strong>mo u ha na sa nai de</strong><br><strong>请别再松开</strong></p><p><strong>願（ねが）いを込（こ）めた空（そら）に</strong><br><strong>ne ga i o ko me te so ra ni</strong><br><strong>寄托心愿的天空</strong></p><p><strong>明日（あした）が来（く）るから</strong><br><strong>a shi ta ga ku ru ka ra</strong><br><strong>明天一定会来到</strong></p><h2 id="part-3" tabindex="-1"><a class="header-anchor" href="#part-3" aria-hidden="true">#</a> Part 3</h2><p><strong>導（みちび）いてくれた 光（ひかり）は 君（きみ）だよ</strong><br><strong>mi chi bi i te ku re ta hi ka ri wa ki mi da yo</strong><br><strong>指引着我前进的光芒 就是你啊</strong></p><p><strong>つられて僕（ぼく）も 走（はし）り出（だ）した</strong><br><strong>tsu ra re te no ku mo ha shi ri da shi ta</strong><br><strong>我也不禁迈步奔向前方</strong></p><p><strong>知（し）らぬ間（ま）に クロスし始（はじ）めた</strong><br><strong>shi ra nu ma ni ku ro su shi ha ji me ta</strong><br><strong>不知不觉间 我们交织在一起</strong></p><p><strong>ほら 今（いま）だ ここで 光（ひか）るなら</strong><br><strong>ho ra i ma da ko ko de hi ka ru na ra</strong><br><strong>看 就是现在 若此处有光芒</strong></p><p><strong>君（きみ）だよ 君（きみ）なんだよ 教（おし）えてくれた</strong><br><strong>ki mi da yo ki mi nan da yo o shi e te ku re ta</strong><br><strong>是你 是你 告诉我</strong></p><p><strong>暗闇（くらやみ）は終（お）わるから</strong><br><strong>ku ra ya mi wa o wa ru ka ra</strong><br><strong>黑暗终究会过去</strong></p><p><strong>君（きみ）だよ 君（きみ）なんだよ 教（おし）えてくれた</strong><br><strong>ki mi da yo ki mi nan da yo o shi e te ku re ta</strong><br><strong>是你 是你 告诉我</strong></p><p><strong>暗闇（くらやみ）も光（ひか）るなら</strong><br><strong>ku ra ya mi mo hi ka ru na ra</strong><br><strong>若能在黑暗中绽放光芒</strong></p><p><strong>星空（ほしぞら）になる</strong><br><strong>ho shi zo ra ni na ru</strong><br><strong>便能化作星空</strong></p><p><strong>悲（かな）しみを笑顔（えがお）に</strong><br><strong>ka na shi mi wo e ga o ni</strong><br><strong>悲伤化作笑颜</strong></p><p><strong>もう隠（かく）さないで</strong><br><strong>mou ka ku sa na i de</strong><br><strong>已经无需隐藏</strong></p><p><strong>煌（きら）めくどんな星（ほし）も</strong><br><strong>ki ra me ku don na ho shi mo</strong><br><strong>漫天闪耀着的星光</strong></p><p><strong>君（きみ）を照（て）らすから</strong><br><strong>ki mi wo te ra su ka ra</strong><br><strong>都会为你照亮前行</strong></p><p><strong>答（こた）えはいつでも偶然（ぐうぜん）？必然（ひつぜん）？</strong><br><strong>ko ta e wa i tsu de mo gun zen hi tsu zen</strong><br><strong>答案是 偶然？必然？</strong></p><p><strong>いつか選（えら）んだ道（みち）こそ</strong><br><strong>i tsu ka e ran da mi ti ko so</strong><br><strong>你曾经选择的路</strong></p><p><strong>運命（うんめい）になる</strong><br><strong>un mei ni na ru</strong><br><strong>才是真正的命运</strong></p><p><strong>握（にぎ）りしめた その希望（きぼう）も不安（ふあん）も</strong><br><strong>ni gi ri shi me ta so no ki bou mo fu an mo</strong><br><strong>紧握在手心的希望也好 不安也罢</strong></p><p><strong>きっと2人（ふたり）を動（うご）かす 光（ひかり）になるから</strong><br><strong>ki tto fu ta ri o u go ka su hi ka ri ni na ru ka ra</strong><br><strong>必定会化作驱使我们前进的光芒</strong></p>',52);function p(b,u){return o(),n("div",null,[i,t(" more "),e])}const m=r(a,[["render",p],["__file","rnzfgm.html.vue"]]);export{m as default};
