import{_ as r}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as t,c as o,d as n,a as s,f as g}from"./app.1e57e5be.js";const a={},i=s("p",null,"《喜剧》是日本男歌手星野源作词、作曲、演唱的一首歌曲。于2022年4月8日发行。该曲也是日本电视动画《间谍×过家家》的片尾曲。",-1),e=g('<h1 id="喜剧" tabindex="-1"><a class="header-anchor" href="#喜剧" aria-hidden="true">#</a> 喜剧</h1><h2 id="part-1" tabindex="-1"><a class="header-anchor" href="#part-1" aria-hidden="true">#</a> Part 1</h2><p><strong>争(あらそ)い合（あ）って 壊（こわ）れかかった</strong><br><strong>a ra so i a tte ko wa re ka ka tta</strong><br><strong>互相争斗临近崩溃</strong></p><p><strong>このお茶（ちゃ）目（め）な星（ほし）で</strong><br><strong>ko no o cha me na ho shi de</strong><br><strong>在那调皮的繁星中</strong></p><p><strong>生（う）まれ落（お）ちた日（ひ）から よそ者（もの）</strong><br><strong>u ma re o chi ta hi ka ra yo so mo no</strong><br><strong>自出生便格格不入</strong></p><p><strong>涙（なみだ）枯（か）れ果（は）てた</strong><br><strong>na mi da ka re ha te ta</strong><br><strong>泪水定格在眼中</strong></p><p><strong>帰（かえ）りゆく場所（ばしょ）は夢（ゆめ）の中（なか）</strong><br><strong>ka e ri yu ku ba sho wa yu me no na ka</strong><br><strong>在梦中觅得归处</strong></p><p><strong>零（こぼ）れ落（お）ちた 先（さき）で出会（であ）った</strong><br><strong>ko bo re o chi ta sa ki de de a tta</strong><br><strong>在零落之地相遇</strong></p><p><strong>ただ秘密（ひみつ）を抱（かか）え</strong><br><strong>ta da hi mi tsu wo ka ka e</strong><br><strong>只是守着秘密</strong></p><p><strong>普通（ふつう）のふりをした あなたと</strong><br><strong>fu tsu u no fu ri wo shi ta a na ta to</strong><br><strong>和装作普通的你</strong></p><p><strong>探（さが）し諦（あきら）めた</strong><br><strong>sa ga shi a ki ra me ta</strong><br><strong>我放弃寻觅</strong></p><p><strong>私（わたし）の居場所（いばしょ）は作（つく）るものだった</strong><br><strong>wa ta shi no i ba sho wa tsu ku ru mo no da tta</strong><br><strong>创造出容身之地</strong></p><p><strong>あの日（ひ）交（か）わした</strong><br><strong>a no hi ka wa shi ta</strong><br><strong>那天彼此交换了</strong></p><p><strong>血（ち）に勝（まさ）るもの</strong><br><strong>chi ni ma sa ru mo no</strong><br><strong>胜过血脉的东西</strong></p><p><strong>心（こころ）たちの契約（けいやく）を</strong><br><strong>ko ko ro ta chi no ke i ya ku wo</strong><br><strong>签订心灵的契约</strong></p><h2 id="part-2" tabindex="-1"><a class="header-anchor" href="#part-2" aria-hidden="true">#</a> Part 2</h2><p><strong>手（て）を繋（つな）ぎ帰（かえ）ろうか</strong><br><strong>te wo tsu na gi ka e ro u ka</strong><br><strong>让我们手牵手一起回家吧</strong></p><p><strong>今日（きょう）は何（なに）食（た）べようか</strong><br><strong>kyo u wa na ni ta be yo u ka</strong><br><strong>今天吃什么好呢</strong></p><p><strong>「こんなことがあった」って</strong><br><strong>「ko n na ko to ga a tta」tte</strong><br><strong>“发生过这样的事情”</strong></p><p><strong>君（きみ）と話（はな）したかったんだ</strong><br><strong>ki mi to ha na shi ta ka tta n da</strong><br><strong>我想和你谈谈</strong></p><p><strong>いつの日（ひ）も  君（きみ）となら喜劇（きげき）よ</strong><br><strong>i tsu no hi mo ki mi to na ra ki ge ki yo</strong><br><strong>无论哪一天 和你在一起就是喜剧</strong></p><p><strong>踊（おど）る軋（きし）むベッドで</strong><br><strong>o do ru ki shi mu bed do de</strong><br><strong>在吱吱作响的床上跳舞</strong></p><p><strong>笑（わら）い転（ころ）げたままで</strong><br><strong>wa ra i ko ro ge ta ma ma de</strong><br><strong>笑得前仰后合</strong></p><p><strong>ふざけた生活（せいかつ）はつづくさ</strong><br><strong>fu za ke ta se i ka tsu wa tsu zu ku sa</strong><br><strong>有趣的生活仍在继续</strong></p><h2 id="part-3" tabindex="-1"><a class="header-anchor" href="#part-3" aria-hidden="true">#</a> Part 3</h2><p><strong>劣（おと）ってると 言（い）われ育（そだ）った</strong><br><strong>o to tte ru to i wa re so da tta</strong><br><strong>伴随着“劣等”一词成长</strong></p><p><strong>このいかれた星（ほし）で</strong><br><strong>ko no i ka re ta ho shi de</strong><br><strong>身居这颗疯狂的星球</strong></p><p><strong>普通（ふつう）のふりをして 気（き）づいた</strong><br><strong>fu tsu u no fu ri wo shi te ki zu i ta</strong><br><strong>我装出一副普通的模样</strong></p><p><strong>誰（だれ）が決（き）めつけた</strong><br><strong>da re ga ki me tsu ke ta</strong><br><strong>又有谁能定义</strong></p><p><strong>私（わたし）の光（ひかり）はただ此処（ここ）にあった</strong><br><strong>wa ta shi no hi ka ri wa ta da ko ko ni a tta</strong><br><strong>我拥有的光芒只在此地</strong></p><p><strong>あの日（ひ）ほどけた</strong><br><strong>a no hi ho do ke ta</strong><br><strong>在那天破解了</strong></p><p><strong>淡（あわ）い呪（のろ）いに</strong><br><strong>a wa i no ro i ni</strong><br><strong>淡淡的诅咒</strong></p><p><strong>心（こころ）からのさよならを</strong><br><strong>ko ko ro ka ra no sa yo na ra wo</strong><br><strong>向其由衷告别</strong></p><h2 id="part-4" tabindex="-1"><a class="header-anchor" href="#part-4" aria-hidden="true">#</a> Part 4</h2><p><strong>顔上（かおあげ）げて帰（かえ）ろうか</strong><br><strong>ka o a ge te ka e ro u ka</strong><br><strong>让我们抬着头一起回家吧</strong></p><p><strong>咲（さ）き誇（ほこ）る花々（はなばな）</strong><br><strong>sa ki ho ko ru ha na ba na</strong><br><strong>目光所及为繁花烂漫</strong></p><p><strong>「こんな綺麗（きれい）なんだ」って</strong><br><strong>「ko n na ki re i na n da」tte</strong><br><strong>“这么漂亮呀”</strong></p><p><strong>君（きみ）と話（はな）したかったんだ</strong><br><strong>ki mi to ha na shi ta ka tta n da</strong><br><strong>我想和你谈谈</strong></p><p><strong>どんな日（ひ）も</strong><br><strong>do n na hi mo</strong><br><strong>在每一天</strong></p><p><strong>君（きみ）といる奇跡（きせき）を 命繋（いのちつな）ぐキッチンで</strong><br><strong>ki mi to i ru ki se ki wo i no chi tsu na gu kic chi n de</strong><br><strong>与你共同创造奇迹</strong></p><p><strong>伝（つた）えきれないままで</strong><br><strong>tsu ta e ki re na i ma ma de</strong><br><strong>心意仍无法传达</strong></p><p><strong>ふざけた生活（せいかつ）はつづく</strong><br><strong>fu za ke ta se i ka tsu wa tsu zu ku</strong><br><strong>嬉闹的生活仍在继续</strong></p><p><strong>仕事（しごと）明（あ）けに</strong><br><strong>shi go to a ke ni</strong><br><strong>下班之后</strong></p><p><strong>歩（あゆ）む共（とも）に</strong><br><strong>a yu mu to mo ni</strong><br><strong>一起走呀</strong></p><p><strong>朝陽（あさひ）が登（のぼ）るわ ああ</strong><br><strong>a sa hi ga no bo ru wa a a</strong><br><strong>朝阳要升起啦</strong></p><p><strong>ありがとうでは</strong><br><strong>a ri ga to u de wa</strong><br><strong>谢谢啦</strong></p><p><strong>足（た）りないから</strong><br><strong>ta ri na i ka ra</strong><br><strong>因为还不够</strong></p><p><strong>手（て）を繋（つな）ぎ</strong><br><strong>te wo tsu na gi</strong><br><strong>那就手牵着手吧</strong></p><h2 id="part-5" tabindex="-1"><a class="header-anchor" href="#part-5" aria-hidden="true">#</a> Part 5</h2><p><strong>さあうちに帰（かえ）ろうか</strong><br><strong>sa a u chi ni ka e ro u ka</strong><br><strong>让我们手牵手一起回家吧</strong></p><p><strong>今日（きょう）は何（なに）食（た）べようか</strong><br><strong>kyo u wa na ni ta be yo u ka</strong><br><strong>今天吃什么好呢</strong></p><p><strong>「こんなことがあった」って</strong><br><strong>「ko n na ko to ga a tta」tte</strong><br><strong>“发生过这样的事情”</strong></p><p><strong>君（きみ）と話（はな）したかったんだ</strong><br><strong>ki mi to ha na shi ta ka tta n da</strong><br><strong>我想和你谈谈</strong></p><p><strong>いつの日（ひ）も 君（きみ）となら喜劇（きげき）よ</strong><br><strong>i tsu no hi mo ki mi to na ra ki ge ki yo</strong><br><strong>无论哪一天 和你在一起就是喜剧</strong></p><p><strong>踊（おど）る軋（きし）むベッドで</strong><br><strong>o do ru ki shi mu bed do de</strong><br><strong>在吱吱作响的床上跳舞</strong></p><p><strong>笑（わら）い転（ころ）げたままで 永遠（えいえん）を探（さが）そうか</strong><br><strong>wa ra i ko ro ge ta ma ma de e i e n wo sa ga so u ka</strong><br><strong>尽情绽放笑容吧 寻找永恒吧</strong></p><p><strong>できるだけ暮（く）らそうか</strong><br><strong>de ki ru da ke ku ra so u ka</strong><br><strong>尽可能地生活吧</strong></p><p><strong>どんなことがあったって</strong><br><strong>do n na ko to ga a tta tte</strong><br><strong>无论发生了什么</strong></p><p><strong>君（きみ）と話（はな）したかったんだ</strong><br><strong>ki mi to ha na shi ta ka tta n da</strong><br><strong>我都想和你谈谈</strong></p><p><strong>いつまでも 君（きみ）となら喜劇（きげき）よ</strong><br><strong>i tsu ma de mo ki mi to na ra ki ge ki yo</strong><br><strong>永远和你在一起就是喜剧</strong></p><p><strong>分（わ）かち合（あ）えた日々（ひび）に</strong><br><strong>wa ka chi a e ta hi bi ni</strong><br><strong>在每个能够分享的日子里</strong></p><p><strong>笑（わらい）し転（ころ）げた先（さき）に</strong><br><strong>wa ra i ko ro ge ta sa ki ni</strong><br><strong>笑得前仰后合</strong></p><p><strong>ふざけた生活（せいかつ）はつづくさ</strong><br><strong>fu za ke ta se i ka tsu wa tsu zu ku sa</strong><br><strong>嬉闹生活还在继续</strong></p>',63);function b(p,k){return t(),o("div",null,[i,n(" more "),e])}const d=r(a,[["render",b],["__file","xiju.html.vue"]]);export{d as default};
